mapper.select:

select item_code, item_title, price, madein, reg_date, item_spec
from (select rownum, a.* from (select item_code, item_title, price, madein, to_char(reg_date,'YYYY-MM-DD') reg_date, item_spec from 
sellingitems_tbl where item_title like '%' || #{name} || '%') a) where rownum>#{start} and rownum  < #{end}

select item_code, item_title, price, madein , 
	to_char(reg_date,'YYYY-MM-DD') "reg_date",	item_spec 
from sellingitems_tbl where item_code = #{code}
